  addModal(config) {
    STORE_LOCATION[getStyleStoreSymbol()] = [...getSnapshot(), config];
    emitChange();
  },
  removeModal(id) {
    STORE_LOCATION[getStyleStoreSymbol()] = getSnapshot().filter(modal => modal.id !== id);
