	return internals.macOS;
};

const isAndroid = (): boolean => {
	return internals.android || internals.androidPhone;
};

export {
	internals,
	supportsTouch,
	isSafari,
	isChrome,
	isFirefox,
	isPhone,
	isTablet,
	isDesktop,
	isCombi,
